);

// Upsert TEIs to DHIS2
each(
  $.patientsUpsert,
  upsert('trackedEntityInstances', $.data.query, $.data.data)
);
fn(state => {
  const {
    data,
    response,
    references,
    patients,
    patientsUpsert,
    placeOflivingMap,
    genderOptions,
    identifiers,
