    }

    const precipitatingEvent1 = findObsByConcept(
      encounter,
      "d5e3d927-f7ce-4fdd-ac4e-6ad0b510b608"
    );
    const otherValue = encounter.obs.find((o) =>
      o.display.includes("Past / Precipitating Events - Other")
