        encounter,
        "fad7b56b-7633-4467-9e6a-17328278b580",
        "10f3b49c-c3dc-4aa7-857f-3f9bb7df8dd0"
      ),
    },
    {
      dataElement: dataEl.f49.diabetesTypeI,
