const findDataValue = (encounter, dataElement, metadataMap) => {
  const { optsMap, optionSetKey, form } = metadataMap;
  const [conceptUuid, questionId] =
    form.dataValueMap[dataElement]?.split("-rfe-") || [];
  const answer = encounter.obs.find((o) => o.concept.uuid === conceptUuid);
  const isObjectAnswer = answer && typeof answer.value === "object";
  const isStringAnswer = answer && typeof answer.value === "string";
  const isNumberAnswer = answer && typeof answer.value === "number";

  if (isStringAnswer || isNumberAnswer) {
    return answer.value;
  }

  if (
    isObjectAnswer &&
    conceptUuid === "722dd83a-c1cf-48ad-ac99-45ac131ccc96" &&
    dataElement === "pN4iQH4AEzk"
  ) {
    console.log("Yes done by psychologist..");

    return "" + answer.value.uuid === "278401ee-3d6f-4c65-9455-f1c16d0a7a98";
  }

  if (
    isObjectAnswer &&
    conceptUuid === "54e8c1b6-6397-4822-89a4-cf81fbc68ce9" &&
    dataElement === "G0hLyxqgcO7"
  ) {
    console.log("True only question detected..", dataElement);
    return answer.value.uuid === "681cf0bc-5213-492a-8470-0a0b3cc324dd"
      ? "true"
      : undefined;
  }

  if (isObjectAnswer) {
    const optionKey = questionId
      ? `${encounter.form.uuid}-${answer.concept.uuid}-rfe-${questionId}`
      : `${encounter.form.uuid}-${answer.concept.uuid}`;

    const matchingOptionSet = optionSetKey[optionKey];

    const opt = optsMap.find(
      (o) =>
        o["value.uuid - External ID"] === answer.value.uuid &&
        o["DHIS2 Option Set UID"] === matchingOptionSet
    );

    // console.log("<-");
    // console.log("optionKey", optionKey);
    // console.log("matchingOptionSet:", matchingOptionSet);
    // console.log("Answer Uuid:", answer.value.uuid);
    // console.log("Answer Display Value:", answer.value.display);
    // console.log("Opt:", opt);
    // console.log("->");

    const matchingOption =
      opt?.["DHIS2 Option Code"] ??
      opt?.["DHIS2 Option name"] ?? 
      answer?.value?.display; 
      //For test mode: remove "?? opt?.["DHIS2 Option name"] ??  answer?.value?.display; " and create a list of matchionOption not found, populate it in a google sheet, log the URL for google sheet at the end of the run and share with admin once a week over email. 
      // OpenMRS Question | Concept External ID | answer.value.display | value.uuid | DHIS2 DE UID | DHIS2 OptionSet UID | Metadata file name


    if (!opt) {
      console.log(
        `No opt found for External id ${answer.value.uuid} and DHIS2 OptionSet ${matchingOptionSet}`
      );
    }
    if (matchingOption !== opt?.["DHIS2 Option Code"]) {
      console.log(
        `No DHIS2 Option Code found for External id ${answer.value.uuid} and DHIS2 OptionSet ${matchingOptionSet}`
      );
    }

    if (["FALSE", "No"].includes(matchingOption)) return "false";
    if (["TRUE", "Yes"].includes(matchingOption)) return "true";

    return matchingOption;
  }

  const isEncounterDate =
    conceptUuid === "encounter-date" &&
    ["CXS4qAJH2qD", "I7phgLmRWQq", "yUT7HyjWurN", "EOFi7nk2vNM"].includes(
      dataElement
    );

  // These are data elements for encounter date in DHIS2
  // F29 MHPSS Baseline v2, F31-mhGAP Baseline v2, F30-MHPSS Follow-up v2, F32-mhGAp Follow-up v2
  if (isEncounterDate) {
    return encounter.encounterDatetime.replace("+0000", "");
  }

  return "";
};

const formEncounters = (formDescription, encounters) => {
  return encounters.filter((e) => e.form.description.includes(formDescription));
};

const buildExitEvent = (encounter, tei, metadataMap) => {
  const { program, orgUnit, trackedEntity, enrollment, events } = tei;
  const { formMaps, dhis2Map, optionSetKey, optsMap } = metadataMap;

  let exitEvents = [];
  const sharedEventMap = {
    program,
    orgUnit,
    trackedEntity,
    enrollment,
    occurredAt: encounter.encounterDatetime.replace("+0000", ""),
  };

  if (encounter.form.name.includes("F49-NCDs Baseline")) {
    const programStage = formMaps[encounter.form.uuid].programStage;
    const metadataMap = {
      events,
      programStage,
      dhis2Map,
      optionSetKey,
      optsMap,
    };
    const eventsMap = mapF49(encounter, metadataMap);
    for (const event of eventsMap) {
      exitEvents.push({ ...sharedEventMap, ...event });
    }
  }
  if (encounter.form.name.includes("F50-NCDs Follow-up")) {
    const programStage = formMaps[encounter.form.uuid].programStage;
    const metadataMap = {
      events,
      programStage,
      dhis2Map,
    };
    const eventsMap = mapF50(encounter, metadataMap);
    for (const event of eventsMap) {
      exitEvents.push({ ...sharedEventMap, ...event });
    }
  }
  if (encounter.form.name.includes("F55-HBV Baseline")) {
    const programStage = formMaps[encounter.form.uuid].programStage;
    const metadataMap = { events, programStage, dhis2Map };
    const eventsMap = mapF55(encounter, metadataMap);
    for (const event of eventsMap) {
      exitEvents.push({ ...sharedEventMap, ...event });
    }
  }
  if (encounter.form.name.includes("F56-HBV Follow-up")) {
    const eventsMap = mapF56(encounter, { events });
    for (const event of eventsMap) {
      exitEvents.push({ ...sharedEventMap, ...event });
    }
  }
  if (encounter.form.name.includes("F58-HCV Follow-up")) {
    const eventsMap = mapF58(encounter, { events });
    for (const event of eventsMap) {
      exitEvents.push({ ...sharedEventMap, ...event });
    }
  }
  if (encounter.form.name.includes("F59-Social Work Baseline")) {
    const eventsMap = mapF59(encounter, { events });
    for (const event of eventsMap) {
      exitEvents.push({ ...sharedEventMap, ...event });
    }
  }
  if (encounter.form.name.includes("F60-Social Work Follow-up")) {
    const eventsMap = mapF60(encounter, { events });
    for (const event of eventsMap) {
      exitEvents.push({ ...sharedEventMap, ...event });
    }
  }
  if (encounter.form.name.includes("F61-Travel medicine")) {
    const eventsMap = mapF61(encounter, { events });
    for (const event of eventsMap) {
      exitEvents.push({ ...sharedEventMap, ...event });
    }
  }
  if (encounter.form.name.includes("F62-Palliative care Baseline")) {
    const eventsMap = mapF62(encounter, { events });
    for (const event of eventsMap) {
      exitEvents.push({ ...sharedEventMap, ...event });
    }
  }
  if (encounter.form.name.includes("F63-Palliative care Follow-up")) {
    const eventsMap = mapF63(encounter, { events });
    for (const event of eventsMap) {
      exitEvents.push({ ...sharedEventMap, ...event });
    }
  }

  return exitEvents;
};
// Prepare DHIS2 data model for create events
fn((state) => {
  const handleMissingRecord = (data, state) => {
    const { uuid, display } = data.patient;

    console.log(uuid, "Patient is missing trackedEntity or enrollment");

    state.missingRecords ??= {};
    state.missingRecords[uuid] ??= {
      encounters: [],
      patient: display,
    };

    state.missingRecords[uuid].encounters.push(data.uuid);
  };
  console.log("Total encounters:", state.encounters.length);

  state.eventsMapping = state.encounters
    .map((encounter, index) => {
      console.log(`Processing encounter ${index}:`, encounter.form.uuid);

      const form = state.formMaps[encounter.form.uuid];
      if (!form?.dataValueMap) {
        return null;
      }
      const program = state.formMaps[encounter.form.uuid].programId;
      const orgUnit = state.formMaps[encounter.form.uuid].orgUnit;
      const patientOuProgram = `${orgUnit}-${program}-${encounter.patient.uuid}`;
      const { trackedEntity, enrollments } =
        state.existingTeis[patientOuProgram] || {};
      const enrollment = enrollments?.[0]?.enrollment;
      const events = enrollments?.[0]?.events;

      if (!trackedEntity || !enrollment) {
        console.log(
          `âŒ Missing TEI data for patient ${encounter.patient.uuid}`
        );

        handleMissingRecord(encounter, state);
        return null;
      }
      console.log(`âœ… Processing encounter ${index} successfully`);

      let formDataValues = Object.keys(form.dataValueMap)
        .map((dataElement) => {
          const value = findDataValue(encounter, dataElement, {
            optsMap: state.optsMap,
            optionSetKey: state.optionSetKey,
            form,
          });
          if (value !== null && value !== undefined && value !== "") {
            return { dataElement, value };
          }
        })
        .filter(
          ({ dataElement, value }) =>
            value &&
            !["pj5hIE6iyAR", "KjgDauY9v4J", "DYTLOoEKRas"].includes(dataElement)
        );

      const customMapping = [
        mapF11(encounter, state.optsMap),
        mapF13(encounter, state.optsMap),
        mapF18(encounter, state.encounters),
        mapF16(encounter),
        mapF17(encounter),
        mapF29(encounter, state.optsMap),
        mapF22(encounter),
        mapF37(encounter),
        mapF38(encounter),
        mapF30F29(encounter, state.allEncounters),
        mapF32F31(encounter, state.allEncounters),
        mapF33F34(encounter, state.allEncounters),
      ]
        .filter(Boolean) // Only include non-empty mappings
        .flat(); // flattening the array

      const latestFormEvent = events.find(
        (e) => e.programStage === form.programStage
      )?.event;

      const encounterEvent = events.find(
        (e) =>
          e.programStage === form.programStage &&
          e.occurredAt === encounter.encounterDatetime.replace("+0000", "")
      )?.event;

      const event =
        form.syncType === "latest" ? latestFormEvent : encounterEvent;
      const formEvent = {
        event,
        program,
        orgUnit,
        trackedEntity,
        enrollment,
        occurredAt: encounter.encounterDatetime.replace("+0000", ""),
        programStage: form.programStage,
        dataValues: [...formDataValues, ...customMapping].filter(
          (d) => d?.value
        ),
      };

      const exitFormEvents = buildExitEvent(
        encounter,
        {
          program,
          orgUnit,
          trackedEntity,
          enrollment,
          events,
        },
        {
          formMaps: state.formMaps,
          dhis2Map: state.dhis2Map,
          optionSetKey: state.optionSetKey,
          optsMap: state.optsMap,
        }
      );

      const mappings = [formEvent, ...exitFormEvents];

      return mappings;
    })
    .flat()
    .filter(Boolean);
  console.log("Final eventsMapping length:", state.eventsMapping.length);

  return state;
});

Create-TEIs-Relationship.body:
fn((state) => {
  state.relationshipsMapping = Object.values(state.createdChildTeis)
    .map((tei) => {
      const omrsPatientUuid = tei?.attributes.find(
        ({ attribute }) => attribute === "AYbfTPYMNJH"
      )?.value;
      const childTei = tei?.trackedEntity;
      const parentTei =
        state.parentTeis[`${state.orgUnit}-${state.program}-${omrsPatientUuid}`]
          ?.trackedEntity;
      const relationshipType = tei?.relationshipType || "cJJTZ51EK24";
      // :white_check_mark: Add validation to ensure both TEIs exist
      if (!childTei || !parentTei) {
        console.log(
          `:warning: Skipping relationship - Missing TEI for patient ${omrsPatientUuid}:`,
          {
            hasChildTei: !!childTei,
            hasParentTei: !!parentTei,
            program: tei?.program,
            orgUnit: tei?.orgUnit,
          }
        );
        return null;
      }
      if (childTei !== parentTei) {
        return {
          from: {
            trackedEntityInstance: {
              trackedEntityInstance: parentTei,
            },
          },
          to: {
            trackedEntityInstance: {
              trackedEntityInstance: childTei,
            },
          },
          relationshipType,
        };
      }
    })
    .filter(Boolean); // :white_check_mark: This now filters out null entries
  console.log(
    `Total relationships to create: ${state.relationshipsMapping.length}`
  );
  return state;
});

// Check if relationship exist
each(
  $.relationshipsMapping,
  get("tracker/relationships", {
    trackedEntity: $.data.from.trackedEntityInstance.trackedEntityInstance,
  }).then((state) => {
    const relationship = state.references.at(-1);
    const toTei = relationship.to.trackedEntityInstance.trackedEntityInstance;
    const hasRelationship = state.data.instances.find(
      (r) => r.to.trackedEntity.trackedEntity === toTei
    );
    state.relationshipsToCreate ??= [];
    if (!hasRelationship) {
      state.relationshipsToCreate.push(relationship);
    }
    return state;
  })
);

// Creating relationship between parent and child tei
each($.relationshipsToCreate || [], create("relationships", $.data));

Create-Child-TEIs.body:
create(
  "tracker",
  {
    trackedEntities: (state) => {
      const childTeis = Object.values(state.childTeisToCreate).map(
        ({ relationshipType, ...tei }) => {
          // Skip enrollment creation for program without registration
          if (tei.program === "od0M4kEn5Rp" && tei.enrollments) {
            const { enrollments, ...teiWithoutEnrollments } = tei;
            return teiWithoutEnrollments;
          }
          return tei;
        }
      );
      return childTeis;
    },
  },
  {
    params: {
      atomicMode: "ALL",
      async: false,
    },
  }
);

fn((state) => {
  state.upsertedTeis =
    state.data.bundleReport.typeReportMap.TRACKED_ENTITY.objectReports.map(
      (report) => report.uid
    );
  return state;
});

each(
  $.upsertedTeis,
  get(`tracker/trackedEntities/${$.data}`, {
    fields: "*",
  }).then((state) => {
    const {
      trackedEntity,
      programOwners,
      enrollments,
      attributes,
      events,
      orgUnit,
    } = state.data || {};

    const program = programOwners.find(
      (po) => po.trackedEntity === trackedEntity && po.orgUnit === orgUnit
    )?.program;

    const patientUuid = attributes.find(
      (a) => a.attribute === "AYbfTPYMNJH"
    ).value;

    const relationshipType = Object.values(state.formMaps).find(
      (form) =>
        form.programId === program &&
        form.orgUnit === orgUnit &&
        form.formName !== "F00-Registration"
    )?.relationshipId;

    // Skip enrollment creation for program without registration
    const skipEnrollment = program === "od0M4kEn5Rp";

    state.createdChildTeis ??= {};
    const patientOuProgram = `${orgUnit}-${program}-${patientUuid}`;
    state.createdChildTeis[patientOuProgram] = {
      relationshipType,
      trackedEntity,
      events: enrollments?.[0]?.events ?? events,
      enrollments: skipEnrollment ? null : enrollments,
      attributes,
      orgUnit,
      program,
    };

    return state;
  })
);

fn((state) => {
  state.existingTeis = { ...state.existingTeis, ...state.createdChildTeis };
  return state;
});
